import { Component, OnInit, AfterViewInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AlertController, LoadingController, NavController, ToastController } from '@ionic/angular';
import { Camera, CameraResultType } from '@capacitor/camera';
import { Observable } from 'rxjs';

import { MasterService } from '@core/services/master.service';
import { StorageService } from '@core/services/storage.service';
import { AuthService } from '@modules/users/services/auth.service';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.page.html',
  styleUrls: ['./sign-up.page.scss'],
})
export class SignUpPage implements OnInit, AfterViewInit {

  registerForm: FormGroup;
  avatar: any;
  countries$: Observable<any[]>;
  idioma = [
    { name: 'EspaÃ±ol (Latinoamerica)', id: 1 },
    { name: 'Ingles (USA)', id: 2 },
    { name: 'Portugues (Brasil)', id: 3 }
  ];

  constructor(
    private fb: FormBuilder,
    private auth: AuthService,
    private ms: MasterService,
    private navCtrl: NavController,
    private alertCtrl: AlertController,
    private toastCtrl: ToastController,
    private loadCtrl: LoadingController,
  ) { }

  async ngOnInit() {
    this.loadForm();
    this.countries$ = this.ms.getMaster('master/countries/');
  }

  ngAfterViewInit() { }

  onSubmit = async () => {
    this.constructImage();
    // this.registerForm.controls.picture.setValue(this.avatar);
    const load = await this.loadCtrl.create({message: 'Loading...'});
    await load.present();
    this.auth.signUp(this.registerForm.value).subscribe(
      async (): Promise<void> => {
        await load.dismiss();
        this.setToast();
      },
      async (err: any) => {
        await load.dismiss();
        if (err.status === 500) {
          this.setToast();
        } else {
          const alert = await this.alertCtrl.create(
            { mode: 'ios', header: 'Error', message: err.error.error || 'Algo errado ocurrio!', buttons: ['OK'] });
          return alert.present();
        }
    });
  };

  loadForm = () => {
    this.registerForm = this.fb.group({
      picture: [''],
      identity: ['0'],
      language: ['', Validators.required],
      token_user: ['0'],
      phone: ['', Validators.required],
      country: ['', Validators.required],
      password: ['', Validators.required],
      type_user: ['COMPANY'],
      email: ['', [Validators.required, Validators.email]],
      last_name: ['', [Validators.required, Validators.minLength(4)]],
      fist_name: ['', [Validators.required, Validators.minLength(4)]],
    });
  };

  takePicture = async () => {
    const image = await Camera.getPhoto({
      quality: 80, allowEditing: false,
      resultType: CameraResultType.DataUrl,
    });
    this.avatar =  image.dataUrl;
  };

  onBack = () => this.navCtrl.navigateRoot('user/signIn');

  private constructImage = () => {
    if (this.avatar) {
      this.registerForm.controls.picture.setValue(this.avatar);
    } else {
      this.registerForm.controls.picture.setValue(this.getImage());
    }
  }

  private setToast = async (): Promise<void> => {
    const toast = await this.toastCtrl.create({
      message: 'Account created, now access with your login and password',
      position: 'top', duration: 1000
    })
    this.navCtrl.navigateBack('/user/signIn');
    toast.present();
  };


  private getImage() {
    return `data:image/png;base64,`;
  }
}
